CREATE TABLE Roles
(
    RoleId TINYINT CONSTRAINT pk_RoleId PRIMARY KEY IDENTITY,
    RoleName VARCHAR(20) CONSTRAINT uq_RoleName UNIQUE NOT NULL
)
