CREATE TABLE Users
(
	[EmailId] VARCHAR(50) CONSTRAINT pk_EmailId PRIMARY KEY,-- chk_ProductId CHECK(ProductId LIKE 'P%'),
	[UserPassword] VARCHAR(15) NOT NULL, -- uq_ProductName UNIQUE NOT NULL,
	[RoleId] TINYINT CONSTRAINT fk_RoleId REFERENCES Roles(RoleId),
    [Gender] CHAR(1) CONSTRAINT chk_Gender CHECK (Gender = 'F' or Gender = 'M') NOT NULL,
    [DateOfBirth] DATE CONSTRAINT chk_DateOfBirth CHECK (DateOfBirth < GETDATE()) NOT NULL,
    [Address] VARCHAR(200) NOT NULL,
)
GO


CREATE TABLE PurchaseDetails
(
   [PurchaseId] BIGINT CONSTRAINT pk_PurchaseId PRIMARY KEY IDENTITY(1000,1),
   [EmailId] VARCHAR(50) CONSTRAINT fk_EmailId_PurchaseDetails REFERENCES Users(EmailId),
   [ProductId] CHAR(4) CONSTRAINT fk_ProductId_PurchaseDetails REFERENCES Products(ProductId),
   [QuantityPurchased] SMALLINT CONSTRAINT chk_QuantityPurchased_PurchaseDetails 
                       CHECK(QuantityPurchased>0) NOT NULL,  
   [DateOfPurchase] SMALLDATETIME CONSTRAINT chk_DateOfPurchase_PurchaseDetails 
                    CHECK (DateOfPurchase<=GETDATE()) DEFAULT GETDATE() NOT NULL
)
